CREATE TABLE IF NOT EXISTS exchangeRate( id INT NOT NULL AUTO_INCREMENT , amount DECIMAL(10,2) NOT NULL ,source_currency VARCHAR(10) NOT NULL ,target_currency VARCHAR(10) NOT NULL ,rate DECIMAL(10,2) NOT NULL ,result_amount DECIMAL(10,2) NOT NULL , username VARCHAR(50) NOT NULL , date_transaction TIMESTAMP, PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS users( id INT NOT NULL AUTO_INCREMENT , username VARCHAR(50) NOT NULL, password VARCHAR(200) NOT NULL ,roles VARCHAR(25) NOT NULL, PRIMARY KEY (id), CONSTRAINT username_unique UNIQUE (username));
